import React, { useState } from 'react';
import { TextField, MenuItem, Button, Checkbox, FormControlLabel, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, IconButton, Typography, Grid } from '@mui/material';
import { Add, Edit, Delete } from '@mui/icons-material';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

const AddExam = () => {
  const [examName, setExamName] = useState('');
  const [examDuration, setExamDuration] = useState('');
  const [questionPicking, setQuestionPicking] = useState('Auto');
  const [topics, setTopics] = useState([{ id: 1, topic: '', easy: '', medium: '', hard: '' }]);
  const [negativeMarking, setNegativeMarking] = useState(0);
  const [marks, setMarks] = useState({ easy: '', medium: '', hard: '' });
  const [passingScore, setPassingScore] = useState('');
  const [showRegistrationForm, setShowRegistrationForm] = useState(false);
  const [captureImage, setCaptureImage] = useState(false);
  const [captureInterval, setCaptureInterval] = useState('');
  const [instructions, setInstructions] = useState('Default instructions...');

  const handleAddTopic = () => {
    setTopics([...topics, { id: topics.length + 1, topic: '', easy: '', medium: '', hard: '' }]);
  };

  const handleDeleteTopic = (id) => {
    setTopics(topics.filter(topic => topic.id !== id));
  };

  const handleTopicChange = (id, field, value) => {
    const newTopics = topics.map(topic => {
      if (topic.id === id) {
        return { ...topic, [field]: value };
      }
      return topic;
    });
    setTopics(newTopics);
  };

  const handleSaveExam = () => {
    // Logic to save the exam details
    console.log({
      examName,
      examDuration,
      questionPicking,
      topics,
      negativeMarking,
      marks,
      passingScore,
      showRegistrationForm,
      captureImage,
      captureInterval,
      instructions,
    });
  };

  return (
    <form>
      <Typography variant="h4">Add Exam</Typography>

      {/* Step 1: Basic Details */}
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <TextField
            label="Exam Name"
            variant="outlined"
            fullWidth
            required
            value={examName}
            onChange={(e) => setExamName(e.target.value)}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            label="Exam Duration (minutes)"
            variant="outlined"
            type="number"
            fullWidth
            required
            value={examDuration}
            onChange={(e) => setExamDuration(e.target.value)}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Question Picking"
            select
            variant="outlined"
            fullWidth
            value={questionPicking}
            onChange={(e) => setQuestionPicking(e.target.value)}
          >
            <MenuItem value="Auto">Auto</MenuItem>
            <MenuItem value="Manual">Manual</MenuItem>
          </TextField>
        </Grid>
      </Grid>

      {/* Conditionally render table for Manual Question Picking */}
      {questionPicking === 'Manual' && (
        <>
          <TableContainer component={Paper} style={{ marginTop: 20 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Topic</TableCell>
                  <TableCell>Easy</TableCell>
                  <TableCell>Medium</TableCell>
                  <TableCell>Hard</TableCell>
                  <TableCell>Total Questions</TableCell>
                  <TableCell>Action</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {topics.map((topic) => (
                  <TableRow key={topic.id}>
                    <TableCell>
                      <TextField
                        label="Topic"
                        variant="outlined"
                        value={topic.topic}
                        onChange={(e) => handleTopicChange(topic.id, 'topic', e.target.value)}
                        fullWidth
                      />
                    </TableCell>
                    <TableCell>
                      <TextField
                        label="Easy"
                        variant="outlined"
                        type="number"
                        value={topic.easy}
                        onChange={(e) => handleTopicChange(topic.id, 'easy', e.target.value)}
                        fullWidth
                      />
                    </TableCell>
                    <TableCell>
                      <TextField
                        label="Medium"
                        variant="outlined"
                        type="number"
                        value={topic.medium}
                        onChange={(e) => handleTopicChange(topic.id, 'medium', e.target.value)}
                        fullWidth
                      />
                    </TableCell>
                    <TableCell>
                      <TextField
                        label="Hard"
                        variant="outlined"
                        type="number"
                        value={topic.hard}
                        onChange={(e) => handleTopicChange(topic.id, 'hard', e.target.value)}
                        fullWidth
                      />
                    </TableCell>
                    <TableCell>
                      {parseInt(topic.easy || 0) + parseInt(topic.medium || 0) + parseInt(topic.hard || 0)}
                    </TableCell>
                    <TableCell>
                      <IconButton onClick={() => handleDeleteTopic(topic.id)}>
                        <Delete />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
          <Button
            variant="contained"
            color="primary"
            startIcon={<Add />}
            onClick={handleAddTopic}
            style={{ marginTop: 20 }}
          >
            Add Topic
          </Button>
        </>
      )}

      {/* Step 2: Advanced Settings */}
      <Typography variant="h6" style={{ marginTop: 30 }}>Advanced Settings</Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <TextField
            label="Negative Marking (%)"
            variant="outlined"
            type="number"
            fullWidth
            required
            value={negativeMarking}
            onChange={(e) => setNegativeMarking(e.target.value)}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            label="Minimum Passing Score (%)"
            variant="outlined"
            type="number"
            fullWidth
            required
            value={passingScore}
            onChange={(e) => setPassingScore(e.target.value)}
          />
        </Grid>
        <Grid item xs={12}>
          <Typography>Assign Marks</Typography>
          <Grid container spacing={2}>
            <Grid item xs={4}>
              <TextField
                label="Easy"
                variant="outlined"
                fullWidth
                value={marks.easy}
                onChange={(e) => setMarks({ ...marks, easy: e.target.value })}
              />
            </Grid>
            <Grid item xs={4}>
              <TextField
                label="Medium"
                variant="outlined"
                fullWidth
                value={marks.medium}
                onChange={(e) => setMarks({ ...marks, medium: e.target.value })}
              />
            </Grid>
            <Grid item xs={4}>
              <TextField
                label="Hard"
                variant="outlined"
                fullWidth
                value={marks.hard}
                onChange={(e) => setMarks({ ...marks, hard: e.target.value })}
              />
            </Grid>
          </Grid>
        </Grid>
        <Grid item xs={12}>
          <FormControlLabel
            control={<Checkbox checked={showRegistrationForm} onChange={(e) => setShowRegistrationForm(e.target.checked)} />}
            label="Registration Form"
          />
        </Grid>
        <Grid item xs={12}>
          <FormControlLabel
            control={<Checkbox checked={captureImage} onChange={(e) => setCaptureImage(e.target.checked)} />}
            label="Capture image during the exam"
          />
          {captureImage && (
            <TextField
              label="Capture Interval (seconds)"
              variant="outlined"
              type="number"
              fullWidth
              value={captureInterval}
              onChange={(e) => setCaptureInterval(e.target.value)}
              style={{ marginTop: 10 }}
            />
          )}
        </Grid>
        <Grid item xs={12}>
          <Typography>Instructions</Typography>
          <ReactQuill value={instructions} onChange={setInstructions} />
        </Grid>
      </Grid>

      {/* Save Exam Button */}
      <Button
        variant="contained"
        color="primary"
        onClick={handleSaveExam}
        style={{ marginTop: 30 }}
      >
        Save Exam
      </Button>
    </form>
  );
};

export default AddExam;
